      equals("31.3".toInt(), 31);
      equals("31.".toInt(), 31);
      equals("-1.02".toInt(), -1);
      equals("009".toInt(), 9);
      equals("0109".toInt(), 109);
      return equals("F".toInt(16), 15);
    });
    return test("#withoutExtension", function() {
      equals("neat.png".withoutExtension(), "neat");
      return equals("not a file".withoutExtension(), "not a file");
    });
  });

}).call(this);

